import React, {useEffect, useState} from 'react';


function Blogs () {
    const [blogs, setBlogs] = useState([]);

    useEffect(()=>{
        fetch('http://localhost:4000/articles')
        .then((response) => response.json())
        .then((data) => {
            setBlogs(data);
        })
    }, []);


    console.log("******blogs****************");
    console.log(blogs);
    console.log("******blogs****************");
    return(
       <>
            {
                blogs.map((blog)=>(
                    <div className="container">
                        <></>
                            <div class="card">
                                <img src={blog.image} class="card-img-top" alt="..."/>
                                <div class="card-body">
                                    <h5 class="card-title">
                                        {blog.title}
                                    </h5>
                                    <p class="card-text">
                                        S{blog.data}
                                    </p>
                                </div>
                            </div>
                    </div>
                ))
            }
       </>
    )
}

export default Blogs;
