import React, {useEffect, useState} from 'react';
import Blog from './Blog';


function Blogs () {
    const [blogs, setBlogs] = useState([]);

    useEffect(()=>{
        fetch('http://localhost:4000/articles')
        .then((response) => response.json())
        .then((data) => {
            setBlogs(data);
        })
    }, []);

    return(
        <div className="container">
            {
                blogs.map((blog)=>(
                    <Blog/>
                ))
            }
       </div>
    )
}

export default Blogs;
